import React from "react";
import axios from "axios";

export default class App extends React.Component {
  state = {
   insulto: ""
  };

  componentDidMount() {
    this.gerarInsulto()
  }

  gerarInsulto = () => {
    axios
      .get("https://www.boredapi.com/api/activity/")
      .then((resposta) => {
        this.setState({
          nome: resposta.data.activity,
          tipo: resposta.data.type,
          participantes: resposta.data.participants,
          preco: resposta.data.price
        });
      })
      .catch((erro) => {
        console.log(erro);
      });
  };

  render() {
    return (
      <div className="App">
      Oi
      </div>
    );
  }
}
