import React from "react";
import "./styles.css";
import axios from "axios";

export default class App extends React.Component {
  state = {
    nome: "",
    tipo: "",
    participantes: "",
    preco: ""
  };

  componentDidMount() {
    this.pegarAtividade()
  }

  pegarAtividade = () => {
    axios
      .get("https://www.boredapi.com/api/activity/")
      .then((resposta) => {
        this.setState({
          nome: resposta.data.activity,
          tipo: resposta.data.type,
          participantes: resposta.data.participants,
          preco: resposta.data.price
        });
      })
      .catch((erro) => {
        console.log(erro);
      });
  };

  render() {
    return (
      <div className="App">
        <p>
          <strong>Nome:</strong> {this.state.nome}
        </p>
        <p>
          <strong>Tipo:</strong> {this.state.tipo}
        </p>
        <p>
          <strong>Participantes:</strong> {this.state.participantes}
        </p>
        <p>
          <strong>Preço:</strong> {this.state.preco}
        </p>
        <button onClick={this.pegarAtividade}>Me dê uma nova atividade!</button>
      </div>
    );
  }
}
