import React from "react";
import axios from "axios";

export default class App extends React.Component {

  state = {
    totalMortos: "",
    novasMortes: "",
    totalRecuperados: ""
  };

  componentDidMount() {
    this.casosCorona()
  }

  casosCorona = () => {
    axios
      .get("https://api.covid19api.com/summary")
      .then((resposta) => {
        console.log(resposta.data.Global.TotalDeaths)
        this.setState({
          totalMortos: resposta.data.Global.TotalDeaths

        });
      })
      .catch((erro) => {
        console.log(erro);
      });
  };

  render() {


    return (
      <div className="App">
        <p>
          <strong>Total de mortos pelo Covid-19:</strong> {this.state.totalMortos}
        </p>

        <button onClick={this.casosCorona}>Atualizar</button>
      </div>
    );
  }
}
