import React from "react";
import axios from "axios";

export default class App extends React.Component {

  state = {
    totalMortos: "",
    novasMortes: "",
    totalRecuperados: ""
  };

  componentDidMount() {
    this.casosCorona()
  }


  casosCorona = () => {
    axios
      .get("https://api.covid19api.com/summary")
      .then((resposta) => {
        this.setState({
          totalMortos: resposta.data.Global.TotalDeaths,
          novasMortes:  resposta.data.Global.NewDeaths,
          totalRecuperados:  resposta.data.Global.TotalRecovered,

        });
      })
      .catch((erro) => {
        console.log(erro);
      });
  };

  render() {


    return (
      <div className="App">
        <p>
          <strong>Total de mortos pelo Covid-19:</strong> {this.state.totalMortos}
        </p>
        <p>
          <strong>Novas mortes do Covid-19</strong> {this.state.novasMortes}
        </p>
        <p>
          <strong>Total recuperados Covid-19:</strong> {this.state.totalRecuperados}
        </p>
      </div>
    );
  }
}
