import React from "react";
import axios from "axios";

export default class App extends React.Component {
  state = {
    insulto: "",
  };

  componentDidMount() {
    this.pegarAtividade()
  }

  gerarInsulto = () => {
    axios
      .get("https://evilinsult.com/generate_insult.php?lang=en&type=json")
      .then((resposta) => {
        this.setState({
          nome: resposta.data.activity,

        });
      })
      .catch((erro) => {
        console.log(erro);
      });
  };

  render() {
    return (
      <div className="App">
        <p>
          <strong>Nome:</strong> {this.state.nome}
        </p>
        <p>
          <strong>Tipo:</strong> {this.state.tipo}
        </p>
        <p>
          <strong>Participantes:</strong> {this.state.participantes}
        </p>
        <p>
          <strong>Preço:</strong> {this.state.preco}
        </p>
        <button onClick={this.pegarAtividade}>Me dê uma nova atividade!</button>
      </div>
    );
  }
}
