import React from "react";
import axios from "axios";

export default class App extends React.Component {
  state = {
    insulto: "",
  };

  componentDidMount() {
    this.gerarInsulto()
  }

  gerarInsulto = () => {
    axios
      .get("https://evilinsult.com/generate_insult.php?lang=en&type=json")
      .then((resposta) => {
        this.setState({
          nome: resposta.data.activity,

        });
      })
      .catch((erro) => {
        console.log(erro);
      });
  };

  render() {
    return (
      <div className="App">
        <p>
          <strong>Nome:</strong> {this.state.nome}
        </p>

        <button onClick={this.gerarInsulto}>Gerar novo insulto</button>
      </div>
    );
  }
}
