import React from "react";
import axios from "axios";

export default class App extends React.Component {
  state = {
    nome: "",
    tipo: "",
    participantes: "",
    preco: ""
  };

  componentDidMount() {
    this.pegarAtividade()
  }

  pegarAtividade = () => {
    axios
      .get("https://www.boredapi.com/api/activity/")
      .then((resposta) => {
        this.setState({
          nome: resposta.data.activity,
          tipo: resposta.data.type,
          participantes: resposta.data.participants,
          preco: resposta.data.price
        });
      })
      .catch((erro) => {
        console.log(erro);
      });
  };

  render() {
    return (
      <div className="App">
     <p>oi</p>
      </div>
    );
  }
}
